import React, { useEffect, useState } from 'react';
import NewExpense from "../NewExpense/NewExpense";
import Expenses from "../Expenses/Expenses";

const DUMMY_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const Home = () => {
  
  const AddExpenseHandler = async (expense) => {
    const response = await fetch('http://localhost:8080/push-data', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ text: expense }),
  });
    

    
 console.log(response)
    
  };
  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:8080/get-data');
      const result = await response.json();
      for(let i in result){
        console.log(result[i])
        if(!(i in DUMMY_EXPENSES)){
          DUMMY_EXPENSES.push(result[i])
        }
      }
      return DUMMY_EXPENSES
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // return React.createElement(
  //   'div',
  //   {},
  //   React.createElement('h2', {}, "Let's get started!"),
  //   React.createElement(Expenses, { items: expenses })
  // );

  return (
    <>
    <NewExpense onAddExpense={AddExpenseHandler} />
    <Expenses items={fetchData} />
    </>
  );
};

export default Home;
