function expenseDate(props){
    const month=props.date.toLocaleString('en-in',{month : 'long'});
    const day=props.date.toLocaleString('en-in',{day: '2-digit'});
    const year=props.date.getFullYear();
    return(<div>
        <div>{month}</div>
        <div>{day}</div>
        <div>{year}</div>
      </div>)
}
export default expenseDate;
